import random as rimport turtle as t##menu = "Choose an option letter"####break_hint = "Subtotals when this many charcters change: "##start_hint = "State names starts with: "####title = "Q Quit\n" + \##       "W Random Walk without erasing\n" + \##       "E Erase screen, restart random walk\n" + \##       "Choice: "####option = t.textinput(menu,title)##option = option.lower()def jumpto(x,y):    t.penup()    t.setpos(x,y)    t.pendown()def direct ():    num = r.randint(0, 9)    angle = num*36    return angledef move(c):    color = ["red", "yellow", "blue", "green"]    t.pencolor(color[c])    t.speed('fastest')    t.hideturtle()    for i in range(1000):        num = r.randint(5, 10)        t.forward(num)        length=direct()        t.left(length)    t.dot(10)    jumpto(0,0)menu = input("Enter Q to quit, W for random walk, and E to erase and random walk: ")c=0while menu != 'Q':    if c==4:        c=0    if menu=="W":        move(c)        c=c+1        menu = input("Enter Q to quit, W for random walk, and E to erase and random walk: ")    else:        c=0        t.clear()        move(c)        c=c+1        menu=input("Enter Q to quit, W for random walk, and E to erase screen and restart")